import{_ as t}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as a,c as n,a as l,b as e,d as o,e as r,r as s}from"./app.c7647394.js";const c={},d={href:"https://zhuanlan.zhihu.com/p/25858189",target:"_blank",rel:"noopener noreferrer"},h=r('<ul><li>专注读书：番茄时间 +0 音乐，3 小时专注阅读 <ul><li>读书就是在学习，是在工作，而不是在休闲，也不是在下午茶</li><li>排除一切干扰，并且尽可能让大脑在读书时候保持紧张思考</li><li>读前准备：看书评，目录 - 前言 | 后记 | 索引</li></ul></li><li>读书笔记 <ul><li>关键内容摘录：核心论点；核心论述逻辑；关键论据</li><li>自我思考：「有道理」、「有问题」的地方</li><li>总结：一句话概括本书，用小卡片放入书中，方便回溯阅读</li><li>不求甚解：从头到尾先看一遍，碰到不懂的地方不要停下来查询或思索。 <ul><li>如果你坚持要了解每一页的意义，才肯往下读，那你一定读不了多少。努力了解细微的重点，反而会错过说得很清楚的大原则。</li></ul></li></ul></li><li>读书目的 <ul><li>知道了这本书讲了什么东西，包括核心思想，论证逻辑，论据和思考框架。做到能够跟朋友大致转述说明的程度；</li><li>把自己的经历和见解，与书里的观点和理论结合起来，这样形成的反馈才是让人真正理解书中内容的关键；</li><li>将书视为工具，需要使用时再看下需要的部分。</li></ul></li></ul><h2 id="_3-2-1-训练法" tabindex="-1"><a class="header-anchor" href="#_3-2-1-训练法" aria-hidden="true">#</a> 3-2-1 训练法</h2><ol><li>随便找一本书，开始阅读，倒计时 3 分钟结束后合上书，找一张纸，画一张思维导图，把刚才你读到的关键点靠回忆记录下来。</li><li>重新阅读刚才阅读的内容，区别是倒计时降低到 2 分钟，也就是你需要用 2 分钟完成刚才 3 分钟读完的内容，并补充刚才的导图里的内容。</li><li>用 1 分钟再次重复上述过程。</li></ol>',3),_={href:"https://sspai.com/post/53852",target:"_blank",rel:"noopener noreferrer"},u={href:"https://sspai.com/post/54065",target:"_blank",rel:"noopener noreferrer"},p=l("h2",{id:"费曼技巧",tabindex:"-1"},[l("a",{class:"header-anchor",href:"#费曼技巧","aria-hidden":"true"},"#"),e(" 费曼技巧")],-1),f=l("ol",null,[l("li",null,"Choose a Concept 选择一个概念；"),l("li",null,"Teach it to a Toddle 假装把它讲给小孩子听；"),l("li",null,"Identify Gaps and Go Back to The Source Material 发现理解不到位的地方，返回原始材料学习；"),l("li",null,"Review and Simplify (Optional) 回顾和简化信息（可选）。")],-1);function m(k,x){const i=s("ExternalLinkIcon");return a(),n("div",null,[l("p",null,[e("阅读技巧："),l("a",d,[e("戴雨森阅读法"),o(i)]),e("、雪球速读法")]),h,l("p",null,[l("a",_,[e("2.4 如何提升信息处理速度 - 少数派"),o(i)])]),l("p",null,[l("a",u,[e("3.5 创建你的信息数据库 - 少数派"),o(i)])]),p,f])}const B=t(c,[["render",m],["__file","Read.html.vue"]]);export{B as default};
